package com.qqzq;import android.test.ActivityInstrumentationTestCase2;import com.qqzq.common.Constants;import com.qqzq.subitem.find.activity.FindTeamActivity;import com.robotium.solo.Solo;import java.util.HashMap;import java.util.Map;/** * Created by jie.xiao on 15/9/5. */public class FindTeamActivityTest extends ActivityInstrumentationTestCase2<FindTeamActivity> {    private FindTeamActivity activity;    private Solo solo;    public FindTeamActivityTest() {        super(FindTeamActivity.class);    }    @Override    protected void setUp() throws Exception {        super.setUp();        solo = new Solo(getInstrumentation(), getActivity());        activity = getActivity();    }    @Override    protected void tearDown() throws Exception {        assertNotNull(solo);        solo.finishOpenedActivities();    }    public void test1_loadTeamListFromBackend(){        Map<String, Object> mParameters = new HashMap<>();        mParameters.put("offset", 0);        mParameters.put("limit", Constants.PAGE_SIZE);        activity.loadTeamListFromBackend(mParameters);    }}